-- 1.8.1升级sql
-- 积木报表加乐观锁---
ALTER TABLE jimu_report
    ADD COLUMN IF NOT EXISTS update_count int NULL DEFAULT 0;
COMMENT ON COLUMN jimu_report.update_count IS '乐观锁版本';
-- 版本默认为0
update jimu_report set update_count = 0 where update_count is null;

-- 在线填报功能 ---
ALTER TABLE jimu_report
    ADD COLUMN submit_form smallint NULL;
COMMENT ON COLUMN jimu_report.submit_form IS '是否填报报表 0不是,1是';

-- 支持自定义目录树---
alter table public.jimu_report
    alter column type type varchar(50) using type::varchar(50);
comment on column public.jimu_report.type is '类型';

-- 支持自定义目录树---
CREATE TABLE IF NOT EXISTS jimu_report_category (
                                      id VARCHAR(64) NOT NULL,
                                      name VARCHAR(100) NOT NULL,
                                      parent_id VARCHAR(64) DEFAULT NULL,
                                      iz_leaf INT DEFAULT NULL,
                                      source_type VARCHAR(10) DEFAULT NULL,
                                      del_flag INT DEFAULT NULL,
                                      create_by VARCHAR(64) DEFAULT NULL,
                                      create_time TIMESTAMP DEFAULT NULL,
                                      update_by VARCHAR(64) DEFAULT NULL,
                                      update_time TIMESTAMP DEFAULT NULL,
                                      tenant_id VARCHAR(11) DEFAULT NULL,
                                      PRIMARY KEY (id)
);

COMMENT ON COLUMN jimu_report_category.id IS '主键';
COMMENT ON COLUMN jimu_report_category.name IS '分类名称';
COMMENT ON COLUMN jimu_report_category.parent_id IS '父级id';
COMMENT ON COLUMN jimu_report_category.iz_leaf IS '是否为叶子节点(0 否 1是)';
COMMENT ON COLUMN jimu_report_category.source_type IS '来源类型( report 积木报表 screen 大屏  drag 仪表盘)';
COMMENT ON COLUMN jimu_report_category.del_flag IS '删除标识(0 正常 1 已删除)';
COMMENT ON COLUMN jimu_report_category.create_by IS '创建人';
COMMENT ON COLUMN jimu_report_category.create_time IS '创建时间';
COMMENT ON COLUMN jimu_report_category.update_by IS '更新人';
COMMENT ON COLUMN jimu_report_category.update_time IS '更新时间';
COMMENT ON COLUMN jimu_report_category.tenant_id IS '租户id';

COMMENT ON TABLE jimu_report_category IS '分类';

